#*********************************************************
#Variables à configurer avant de lancer le script
#*********************************************************

#*********************************************************
# Version du programme : Red ou Blue
#*********************************************************
$NHVersion = "Blue"

#*********************************************************
# Préciser le répertoire local et le partage réseau que le logiciel va scruter pour désactiver le script
#*********************************************************
#Répertoire local
$AutoDesarmFolder = "C:\tmp\nh"
#Partage réseau
$AutoDesarmShare = "\\xxxx.xxxxx.xxxx\test$\cache\NH"

#*********************************************************
# Date limite pour le compteur de gauche, effacement des fichiers
#*********************************************************
$LeftDateLimitTime = "DD/MM/YYYY"

#*********************************************************
# Date limite pour le compteur de droite, diffusion des fichiers
#*********************************************************
$RightDateLimitTime = "DD/MM/YYYY"

#*********************************************************
# Limite de temps à laquelle le script se ferme automatiquement (Défaut 180 min)
#*********************************************************
$KillTime = (Get-Date).AddMinutes(180)

#*********************************************************
# Date à partir de laquelle le script ne pourra plus être lancé (format dd/MM/yyyy HH:mm)
#*********************************************************
$NoScriptTime = "DD/MM/YYYY HH:MM"

#*********************************************************
# Touche à appuyer en plus de la touche "Ctrl" pour fermer la fenêtre (N par défaut)
#*********************************************************
$KeyToClose = "N"


#******************************************************************************************************************
#                                 Ne rien modifier en dessous de cette ligne.
#******************************************************************************************************************
#*********************************************************
# Appel des composants nécessaires au fonctionnement du programme
#*********************************************************

Add-Type -AssemblyName System.Windows.Forms
Add-Type -AssemblyName System.Drawing
Add-Type -AssemblyName presentationframework

# Masquage de la fenêtre de ligne de commande PowerShell afin de ne pas pouvoir fermer le programme facilement

$consoleHandle = (Get-Process -Id $pid).MainWindowHandle
$win32ShowWindowAsync = Add-Type -MemberDefinition @"
[DllImport("user32.dll")]
public static extern bool ShowWindowAsync(IntPtr hWnd, int nCmdShow);
"@ -Name "Win32ShowWindowAsync" -Namespace Win32Functions -PassThru
$win32ShowWindowAsync::ShowWindowAsync($consoleHandle, 0)


# Exécution du script seulement s'il n'y a aucun fichier dans les dossiers définis précédemment

$FileInFolder = (Get-ChildItem $AutoDesarmFolder -ErrorAction SilentlyContinue).Count #Nombre de fichiers présents dans le dossier local de désamorçage
$FileInShare = (Get-ChildItem $AutoDesarmShare -ErrorAction SilentlyContinue).Count #Nombre de fichiers présents dans le dossier réseau de désamorçage

if (($FileInFolder -lt 1) -and ($FileInShare -lt 1) -and ((Get-Date) -lt ([datetime]::ParseExact($NoScriptTime,'dd/MM/yyyy HH:mm',$null))))
{

# Arrêt de l'explorateur Windows et attente de quelques secondes

taskkill /F /IM explorer.exe
Start-Sleep -Seconds 5

# Affichage d'un écran noir sur tous les écrans

$screens = [System.Windows.Forms.Screen]::AllScreens

foreach ($screen in $screens) 
{
    $form = New-Object System.Windows.Forms.Form
    $form.Text = "NHCryptoLocker RS"
    $form.BackColor = "Black"
    $form.FormBorderStyle = 'None'
    $form.WindowState = 'Maximized'
    $form.StartPosition = 'Manual'
    $form.DesktopLocation = $screen.Bounds.Location
    $form.Size = $screen.Bounds.Size
    $form.TopMost = $true
    $form.ShowInTaskbar = $false
    $form.Show()
}
