    # Geestion des contrôles commun
    #*********************************************************
    # Fermeture de la fenêtre
    $CloseKeyEvents = {
        if ($_.Key -eq $KeyToClose -and $_.KeyboardDevice.Modifiers -eq "Ctrl") 
        {            
            $this.Add_Closing({$_.Cancel=$false})
            $this.Close()
        }
    }
    # Affichage Projet NH
    $NHKeyEvents = {
        if ($_.Key -eq "H" -and $_.KeyboardDevice.Modifiers -eq "Ctrl") 
