    $RightCountDown = $Window.FindName("TimerRight")
    $LeftDate = Get-Date -Date $LeftDateLimitTime
    $RightDate = Get-Date -Date $RightDateLimitTime
    # Initialisation du compteur de gauche
    $timer1=New-Object System.Windows.Forms.Timer
    $timer1.Interval=1000
    $timer1.Add_Tick({$LeftCountDown.Content="{0:dd}:{0:hh}:{0:mm}:{0:ss}" -f ($LeftDate - (Get-Date))})
    $timer1.Start()
    # Initialisation du Compteur de droite
    $timer2=New-Object System.Windows.Forms.Timer
    $timer2.Interval=1000
    $timer2.Add_Tick({
    $RightCountDown.Content="{0:dd}:{0:hh}:{0:mm}:{0:ss}" -f ($RightDate - (Get-Date))
        #*********************************************************
        # Gestion de l'autodestruction
        #*********************************************************
        # Fermeture de l'application en mettant un fichier dans le répertoire local ou réseau
        # Vérification toutes les secondes de la présence ou non d'un fichier.
        #*********************************************************
        $FileInFolder = (Get-ChildItem $AutoDesarmFolder -ErrorAction SilentlyContinue).Count
        $FileInShare = (Get-ChildItem $AutoDesarmShare -ErrorAction SilentlyContinue).Count
        if (($FileInFolder -ge 1) -or ($FileInShare -ge 1) -or ($KillTime -lt (Get-Date)))
        {
            #Désactivation de la protection contre les fermetures
            $Window.Add_Closing({$_.Cancel=$false})
            #Fermeture de la fenêtre
            $Window.Close()
        }
    })
    $timer2.Start()
    #*********************************************************
    # Fin de génération des compteurs
